// Switch condition

// syntax:
// switch(variable){
//     case 1:
//         statement
//    case 2:
//         statement
//     case 3:
//         statement  
//     default
//         statement  
// }

var fruit="Apple"
switch(fruit){
    case 'Apple':
        console.log("this is apple")
        break
    case 'Banana':
        console.log("this is banana")
        break
    case 'Mango': 
        console.log("this is mango")
        break
    default:
        console.log("this is not a fruit")
}

let day = 4;

switch (day) {
    case 1:
        console.log("Monday");
        break;
    case 2:
        console.log("Tuesday");
        break;
    case 3:
        console.log("Wednesday");
        break;
    case 4:
        console.log("Thursday");
        break;
    case 5:
        console.log("Friday");
        break;
    case 6:
        console.log("Saturday");
        break;
    case 7:
        console.log("Sunday");
        break;
    default:
        console.log("Invalid day");
}


